import React, {useEffect, useRef, useState} from 'react';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import clsx from 'clsx';
import Collapse from '@material-ui/core/Collapse';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import Divider from '@material-ui/core/Divider';
import AddComment from '@material-ui/icons/AddComment';
import InsertEmoticon from '@material-ui/icons/InsertEmoticon';
import AddBox from '@material-ui/icons/AddBox';
import {useAuth0} from "@auth0/auth0-react";

const useStyles1 = makeStyles((theme) => ({
    root: {
        maxWidth: '100%',
        borderTop: '1px solid white',
        borderRadius: '0'
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
}));

const CommentsBlock = ({likes, coms, ids}) => {
    const classes = useStyles1();
    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    const renderComment = (author, text, i) => {
        return (
            <div key={i}>
                <Grid container alignItems="center" >
                    <Typography variant="h5" style={{fontWeight: 'bold'}} >{author}:&nbsp;&nbsp;</Typography>
                    <Typography variant="h5" >{text}</Typography>
                </Grid>
                <hr/>
            </div>
        );
    };

    return (
        <Card className={classes.root}>
            <CardContent>
                <CustomizedInputBase ids={ids}/>
            </CardContent>
            <CardActions disableSpacing>

                <Grid container alignItems="center">
                    <IconButton aria-label="like">
                        <FavoriteIcon />
                    </IconButton>
                    <Typography variant="h5">{likes.length}</Typography>
                </Grid>

                <IconButton
                    className={clsx(classes.expand, {
                        [classes.expandOpen]: expanded,
                    })}
                    onClick={handleExpandClick}
                    aria-expanded={expanded}
                    aria-label="show more"
                >
                    <ExpandMoreIcon />
                </IconButton>
            </CardActions>
            <Collapse in={expanded} timeout="auto" unmountOnExit>
                <CardContent>
                    <Typography variant="h5" paragraph>Comments:</Typography>

                    {coms.map( ( item, i ) => renderComment( item.author, item.text, i ) )}

                </CardContent>
            </Collapse>
        </Card>
    );
};

export default CommentsBlock;

const useStyles2 = makeStyles((theme) => ({
    root: {
        display: 'flex',
        alignItems: 'center',
        maxWidth: '100%',
    },
    input: {
        marginLeft: theme.spacing(1),
        flex: 1,
    },
    iconButton: {
        padding: 10,
    },
    divider: {
        height: 28,
        margin: 4,
    },
}));

const CustomizedInputBase = ({ids}) => {
    const classes = useStyles2();
    const {user} = useAuth0;
    const valuePost = useRef(null);
    const [textValue, setTextValue] = useState('');

    let sendValue = (e) => {
        setTextValue(e.target.value);
    };

    const addComment = (id) => {

        if (commentToAdd.length !== 0) {
            axios.post("http://localhost:4000/api/comments", {
                params: {
                    id: id, mail: window.location.pathname.split("/").slice(2)[0],
                    userName: user.nickname, userText: textValue
                }
            })
                .then(response => {
                    valuePost.current.value = '';
                    textValue('');
                    // Bad Item structure! working block(refresh comments)
                    // axios.put("http://localhost:4000/api/newcomment/",
                    //     { params: { mail: window.location.pathname.split("/").slice(2)[0], id: id }})
                    //     .then(response => {
                    //         let temp = response.data.posts.find(item => item.postComId === id).comments;
                    //
                    //         setCommentsList(temp)
                    //     });
                });
        }


        return (
            <Paper component="form" className={classes.root}>
                <IconButton disabled className={classes.iconButton} aria-label="menu">
                    <AddComment/>
                </IconButton>
                <InputBase innerRef={valuePost} value={textValue} onChange={sendValue}
                           className={classes.input}
                           placeholder="Add Your Comment"
                           inputProps={{'aria-label': 'Add'}}
                />
                <IconButton disabled type="button" className={classes.iconButton} aria-label="search">
                    <InsertEmoticon/>
                </IconButton>
                <Divider className={classes.divider} orientation="vertical"/>
                <IconButton color="primary" className={classes.iconButton} aria-label="AddBox"
                            onClick={addComment.bind(this, ids)}>
                    <AddBox style={{color: 'green'}}/>
                </IconButton>
            </Paper>
        );
    }
}
