import React from 'react';
import {useAuth0} from "@auth0/auth0-react";
import history from "./history";
import { Router, Route, Switch, Redirect } from 'react-router-dom';
import WaveButton from "../WaveButton/WaveButton";
import UserPage from "../UserPage/UserPage";
import CircularProgress from '@material-ui/core/CircularProgress';
import Grid from '@material-ui/core/Grid';

const App = () => {
    const { isAuthenticated, isLoading, user } = useAuth0();

    if (isLoading) {
        return (
            <Grid container alignItems="center" justify="center"
                  style={{position: "absolute", width: '100%', height: '100%'}}>
                <CircularProgress/>
            </Grid>
        );
    }

    // if (isAuthenticated) {
    //     return (
    //         <div>
    //             <Redirect to="/user" />
    //         </div>
    //     );
    // }

    return (
        <div>
            <Router history={history}>
                <Switch>
                    {/*{isAuthenticated ?*/}
                    {/*    <Redirect exact from="/" to={`/user/${user.name}/`}/> :*/}
                    {/*    <Redirect  exact from="/" to="/login" />}*/}

                    {/*<Route  path="/login" component={WaveButton}/>*/}

                    {/*{isAuthenticated ?*/}
                    {/*    <Route exact path={`/user/:user${user.name}/`} component={UserPage}/> : ''*/}
                    {/*}*/}

                    <Route exact path="/">
                        {isAuthenticated ? <Redirect exact to={`/${user.name}/`} /> : <WaveButton />}
                    </Route>

                    {/*<Route exact path="/">*/}
                    {/*    {isAuthenticated ? <Redirect to={`/user/${user.name}/`} /> : <UserPage />}*/}
                    {/*</Route>*/}

                    { isAuthenticated && <Route path={`/:user${user.name}/`} component={UserPage} /> }

                </Switch>
            </Router>
        </div>
    );
};

export default App;
