import React, {useEffect, useState} from 'react';
import { connect } from "react-redux";
import { getData } from "../../store/reducers";
import { selectDataList } from "../../store/actions";

import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import CameraIcon from '@material-ui/icons/PhotoCamera';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import CloseRounded from '@material-ui/icons/CloseRounded';
import FollowersBar from "../FollowersBar/FollowersBar";
import Avatar from "@material-ui/core/Avatar";
import {useAuth0} from "@auth0/auth0-react";



const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
    },
    icon: {
        marginRight: theme.spacing(2),
    },
    heroContent: {
        backgroundColor: theme.palette.background.paper,
        padding: theme.spacing(8, 0, 6),
        marginTop: '20px'
    },
    heroButtons: {
        marginTop: theme.spacing(4),
    },
    cardGrid: {
        paddingTop: theme.spacing(8),
        paddingBottom: theme.spacing(8),
    },
    card: {
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
    },
    cardMedia: {
        paddingTop: '56.25%', // 16:9
    },
    cardContent: {
        flexGrow: 1,
    },
    footer: {
        backgroundColor: theme.palette.background.paper,
        padding: theme.spacing(6),
    },
    avatar: {
        width: theme.spacing(5),
        height: theme.spacing(5),
        marginRight: '10px'
    },
}));

const cards = [1, 2, 3, 4, 5, 6, 7, 8, 9];

const UserPage = ({getData, data, history}) => {
    const classes = useStyles();
    const { logout, user } = useAuth0();
    const [bottomMenuShown, setBottomMenuShown] = useState(false);
    const [isRenderedDataAuthenticatedUser, setIsRenderedDataAuthenticatedUser] = useState(false);
    const [pathForDataRender, setPathForDataRender] = useState("");
    const [renderedData, setRenderedData] = useState({});

    useEffect(() => {
        setPathForDataRender(history.location.pathname.split("/").slice(2)[0]);// window.location.pathname.split("/").slice(2)
        getData();

        let tempData = data.find(item => item.mail === pathForDataRender);

        setRenderedData(tempData);
        pathForDataRender === user.email ? setIsRenderedDataAuthenticatedUser(true) : setIsRenderedDataAuthenticatedUser(false);
    },[data]);



    return (
        <React.Fragment>
            <CssBaseline />
            <AppBar position="fixed" >
                <Toolbar>
                    <IconButton edge="start" className={classes.menuButton}
                                color="inherit" aria-label="menu"
                                onClick={() => setBottomMenuShown(!bottomMenuShown)}>
                        { !bottomMenuShown ? <MenuIcon /> : <CloseRounded/> }
                    </IconButton>
                    <Avatar src={user.picture} className={classes.avatar} />
                    <Typography variant="h6" className={classes.title}>
                        {user.email}
                    </Typography>
                    <Button color="inherit" onClick={logout}>Log out</Button>
                </Toolbar>
            </AppBar>
            <FollowersBar isMenuShown={bottomMenuShown}/>
            <main>
                {/* Hero unit */}
                <div className={classes.heroContent}>
                    <Container maxWidth="sm">
                        <Typography component="h1" variant="h2" align="center" color="textPrimary" gutterBottom>
                            {renderedData.name}
                        </Typography>

                        <Avatar src={renderedData.ava} className={classes.avatar} />

                        <div className={classes.heroButtons}>
                            <Grid container spacing={2} justify="center">
                                <Grid item>
                                    <Button variant="contained" color="primary">
                                        Main call to action
                                    </Button>
                                </Grid>
                                <Grid item>
                                    <Button variant="outlined" color="primary">
                                        Secondary action
                                    </Button>
                                </Grid>
                            </Grid>
                        </div>
                    </Container>
                </div>
                <Container className={classes.cardGrid} maxWidth="md">
                    {/* End hero unit */}
                    <Grid container spacing={4}>
                        {cards.map((card) => (
                            <Grid item key={card} xs={12} sm={6} md={4}>
                                <Card className={classes.card}>
                                    <CardMedia
                                        className={classes.cardMedia}
                                        image="https://source.unsplash.com/random"
                                        title="Image title"
                                    />
                                    <CardContent className={classes.cardContent}>
                                        <Typography gutterBottom variant="h5" component="h2">
                                            Heading
                                        </Typography>
                                        <Typography>
                                            This is a media card. You can use this section to describe the content.
                                        </Typography>
                                    </CardContent>
                                    <CardActions>
                                        <Button size="small" color="primary">
                                            View
                                        </Button>
                                        <Button size="small" color="primary">
                                            Edit
                                        </Button>
                                    </CardActions>
                                </Card>
                            </Grid>
                        ))}
                    </Grid>
                </Container>
            </main>
            {/* Footer */}
            <footer className={classes.footer}>
                <Typography variant="h6" align="center" gutterBottom>
                    Footer
                </Typography>
                <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
                    Something here to give the footer a purpose!
                </Typography>
                {/* Copyright */}
                <Typography variant="body2" color="textSecondary" align="center">
                    {'Copyright © '}
                    <Link color="inherit" href="https://material-ui.com/">
                        Your Website
                    </Link>{' '}
                    {new Date().getFullYear()}
                    {'.'}
                </Typography>
                {/* Copyright */}
            </footer>
            {/* End footer */}
        </React.Fragment>
    );
};
const mapStateToProps = (state) => ({
    data: selectDataList(state)
});


export default connect(mapStateToProps, {getData})(UserPage);
